create table notification
(
    id      int auto_increment
        primary key,
    source  varchar(255)   not null comment '發送者',
    target  varchar(255)   not null comment '接收者(手機號)',
    type    int            not null comment '通知類型:
0: [new] 系統新消息(版本更新/功能更新)
1: [common] 系統通知(用戶請求回復)
2: [warn] 系統警告(商品舉報/留言舉報/帳號可疑操作)',
    date    datetime       not null,
    status  int            not null comment '是否已讀:
0: 已讀
1: 未讀',
    title   varchar(1000)  not null comment '消息標題',
    content varchar(10000) not null comment '消息內容',
    constraint notification_id_uindex
        unique (id)
);

